<script>
import TheHeaderSearch from '~/components/TheHeaderSearch'

export default {

    components: {
        TheHeaderSearch
    },

    computed: {
        meta () {
            return this.$store.state.meta
        },
        pages () {
            return this.$store.state.pages
        }
    },

    mixins: {
        homeScrollTop: Function
    }
}
</script>

<style lang="scss" scoped>
@import '~assets/css/vars.scss';

header {
    font-family: 'Roboto', sans-serif;
    left: 0;
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 10;
    > div {
        align-items: center;
        border-bottom: 1px solid #d8d8d8;
        background-color: #fff;
        display: flex;
        height: 80px;
        margin: 0 auto;
        max-width: $containerWidth;
        padding: 0 32px;
        transition: padding-top 0.5s, padding-bottom 0.5s;

        @media (max-width: 700px) {
            height: 60px;
            padding: 16px;
            justify-content:center;
        }
    }

    .blog-title {
        margin-right: 32px;

        svg {
            width: 100px;
            height: 110px;
        }

        @media (max-width: 700px) {
            margin-right: 0px;
            svg {
                width: 70px;
                height: 90px;
            }
        }

        h1 {
            color: #666;
            cursor: pointer;
            font-size: 1.1rem;
            font-weight: 400;
        }
    }

    nav {
        flex-shrink: 0;

        @media (max-width: 700px) {
            display: none;
        }
    }

    a {
        border-color: lighten($primary, 40%);
        color: #0b0b0e;
        font-size: 0.9rem;
        font-weight: 300;
        text-decoration: none;
        transition: color 0.1s, border-color 0.1s;

        &:hover {
            color: #555;
            border-color: #555;
        }

        &.nuxt-link-active:not(.blog-title) {
            padding-bottom: 4px;
            border-bottom: 2px solid;
        }

        & + a {
            margin-left: 32px;

            @media (max-width: 700px) {
                margin-left: 16px;
            }
        }
    }
}
</style>
